import React, { useState } from 'react'; // eslint-disable-next-line 
import { Collapse, Fade, Paper, Typography } from '@mui/material';
 // eslint-disable-next-line 
import { makeStyles } from '@mui/styles';
import AddCardorListTitle from './AddCardorListTitle'

const useStyle = makeStyles(theme => ({
  title: {
   display:"flex",
   justifyContent:"space-around",
   margin:"10px"
  },
  titleText: {
    fontSize: "1.2rem",
    fontWeight:"bold"
  }

}));

const AddCardorList = () => {
  const classes = useStyle();
   // eslint-disable-next-line 
  const [open, setOpen] = useState(false);
  return (
    <div class={classes.title}>AddCardorList
    <Collapse in={open}>
      <AddCardorListTitle/>
    </Collapse>
    <Collapse in={!open}>
    <Paper class={classes.AddCardorListTitle}>
      <Typography>
        + Add a card
      </Typography>
    </Paper>
    </Collapse>
    </div>
  )
}

export default AddCardorList
